# Create a simple length validator
@register_validator("min_length")
class MinLengthValidator(Validator):
    """Validator to ensure text meets minimum length requirement."""

    def __init__(self, min_length: int = 10, **kwargs):
        # require_rc=False means no .wallrc config file needed
        super().__init__(require_rc=False, **kwargs)
        self.min_length = min_length

    def _validate(self, value: str, metadata: dict):
        """Validate that value meets minimum length."""
        if not isinstance(value, str):
            return FailResult(
                error_message="Value must be a string",
                metadata=metadata
            )

        if len(value) < self.min_length:
            return FailResult(
                error_message=f"Text too short: {len(value)} < {self.min_length} characters",
                metadata={**metadata, "actual_length": len(value), "required_length": self.min_length}
            )

        return PassResult(metadata=metadata)
